const createContentInitialState = (json) => {
  let pages = {};
  let currentPage = -1;
  let pageCount = 0;
  let elementCount = 0;
  const { elems } = json;

  elems.forEach((val, i, arr) => {
    if(val.TYPE === 26){
      pages = {
        ...pages,
        [pageCount]: {
          index: i,
          seqno: val.SEQUENCENO,
          pagesetting: val.PAGESETTING,
          autoId: val.AUTOID,
          content: val.CONTENT,
          elems: {}
        }

      };
      currentPage = pages[pageCount];
      elementCount = 0;
      pageCount++;
    }else{
      currentPage.elems ={
        ...currentPage.elems,
        [`${currentPage.autoId}_${val.CSS_ID}`]: {
          PAGE_INDEX: elementCount,
          PARENT: currentPage.autoId,
          TYPE: val.TYPE,
          CONTENT: val.CONTENT,
          SCOID: val.SCOID,
          CSID: val.CSID,
          CSS_WIDTH: val.CSS_WIDTH,
          CSS_VALIGN: val.CSS_VALIGN,
          CSS_HIDE: val.CSS_HIDE,
          CSS_BGCOLOR: val.CSS_BGCOLOR,
          CSS_BORDER: val.CSS_BORDER,
          CSS_BORDERCOLOR: val.CSS_BORDERCOLOR,
          CSS_HALIGN: val.CSS_HALIGN,
          CSS_MARGINLEFT: val.CSS_MARGINLEFT,
          CSS_MARGINRIGHT: val.CSS_MARGINRIGHT,
          CSS_MARGINTOP: val.CSS_MARGINTOP,
          CSS_MARGINBOTTOM: val.CSS_MARGINBOTTOM,
          CSS_DELAY: val.CSS_DELAY,
          CSS_ENTRY: val.CSS_ENTRY,
          CSS_HIDDEN: val.CSS_HIDDEN,
          CSS_ID: val.CSS_ID,
          CSS_MAXWIDTH: val.CSS_MAXWIDTH[i]
        }
      };
      elementCount++;
    }
  });
  return pages;
}
let initialPages = createContentInitialState(oPortalModuleContent)
export { initialPages as pages };
